bool encoder_update_user(uint8_t index, bool clockwise) {
    if (index == 0) { /* First encoder */
        if (clockwise) {
            tap_code(KC_WH_R);
        } else {
            tap_code(KC_WH_L);
        }
    } else if (index == 1) { /* Second encoder */
        if (clockwise) {
           tap_code(KC_VOLU);
        } else {
           tap_code(KC_VOLD);
        }
    }
    return false;
}



